                    <UserDiv user={{ nom: 'John', prenom: 'Doe' }} />
                    <UserDiv user={{ nom: 'John', prenom: 'Doe' }} />
                    <UserDiv user={{ nom: 'John', prenom: 'Doe' }} />
                </div>
            </div>
        </div>
    );
};

export default UtilisateursPage;
