import React from 'react';

const UtilisateursAddPage = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [users] = useState([
        { nom: 'John', prenom: 'Doe' },
        { nom: 'Jane', prenom: 'Smith' },
        { nom: 'Alice', prenom: 'Johnson' },
        { nom: 'Bob', prenom: 'Brown' },
        { nom: 'Charlie', prenom: 'Davis' },
        { nom: 'Eve', prenom: 'Wilson' },
        { nom: 'Frank', prenom: 'Taylor' }
    ]);

    const handleSearch = (event) => {
        setSearchTerm(event.target.value);
    };

    const filteredUsers = users.filter(user =>
        user.nom.toLowerCase().includes(searchTerm.toLowerCase()) ||
        user.prenom.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div>
            <h1>Les utilisateurs</h1>

            <div class="search-container">
                <input type="text" placeholder="Rechercher un utilisateur" value={searchTerm} onChange={handleSearch} />
                <button>Ajouter un utilisateur</button>
            </div>

            <div class="utilisateurs-container">
                <div class="utilisateurs-list">
                    {
                        filteredUsers.map((user, index) => (
                            <UserDiv key={index} user={user} />
                        ))
                    }
                </div>
            </div>
        </div>
    );
};

export default UtilisateursAddPage;
