import React from 'react';

const UserDiv = ({ user }) => {
  return (
    <div class="utilisateurs-item">
        <div className='utilisateurs-item-content'>
            <p class="nom">{user.nom}</p>
            <p class="prenom">{user.prenom}</p>
        </div>
        <div class="utilisateurs-item-actions">
            <button>Modifier</button>
            <button>Supprimer</button>
        </div>
    </div>
  );
};

const UtilisateursPage = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [users, setUsers] = useState([
        { nom: 'John', prenom: 'Doe' },
        { nom: 'Jane', prenom: 'Smith' },
        { nom: 'Alice', prenom: 'Johnson' },
        { nom: 'Bob', prenom: 'Brown' },
        { nom: 'Charlie', prenom: 'Davis' },
        { nom: 'Eve', prenom: 'Wilson' },
        { nom: 'Frank', prenom: 'Taylor' }
    ]);

    const handleSearch = (event) => {
        setSearchTerm(event.target.value);
    };

    const filteredUsers = users.filter(user =>
        user.nom.toLowerCase().includes(searchTerm.toLowerCase()) ||
        user.prenom.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div>
            <h1>Les utilisateurs</h1>

            <div class="search-container">
                <input type="text" placeholder="Rechercher un utilisateur" value={searchTerm} />
                <button>Rechercher</button>
            </div>

            <div class="utilisateurs-container">
                <div class="utilisateurs-list">
                    <UserDiv user={{ nom: 'John', prenom: 'Doe' }} />
                    <UserDiv user={{ nom: 'John', prenom: 'Doe' }} />
                    <UserDiv user={{ nom: 'John', prenom: 'Doe' }} />
                    <UserDiv user={{ nom: 'John', prenom: 'Doe' }} />
                    <UserDiv user={{ nom: 'John', prenom: 'Doe' }} />
                    <UserDiv user={{ nom: 'John', prenom: 'Doe' }} />
                    <UserDiv user={{ nom: 'John', prenom: 'Doe' }} />
                </div>
            </div>
        </div>
    );
};

export default UtilisateursPage;
